mapper.update:
UPDATE feeds_member_post_fandom
SET like_count=IFNULL(like_count,0)+1
WHERE id = #{id,jdbcType=BIGINT}

UPDATE feeds_member_post_fandom
SET like_count=CASE WHEN IFNULL(like_count,0)-1 < 0 THEN 0 ELSE IFNULL(like_count,0)-1 END
WHERE id = #{id,jdbcType=BIGINT}

UPDATE feeds_member_post_fandom
SET comment_count=IFNULL(comment_count,0)+1
WHERE id = #{id,jdbcType=BIGINT}

UPDATE feeds_member_post_fandom
SET comment_count=CASE WHEN IFNULL(comment_count,0)-1 < 0 THEN 0 ELSE IFNULL(comment_count,0)-1 END
WHERE id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT T1.id, T1.post_title, T1.post_content, T1.post_newsfeeds, T1.post_time, T2.sn ui_sn, T2.nickname ui_nickname, T2.member_avatar ui_member_avatar,
(SELECT count(1) FROM feeds_member_post_like WHERE post_id = T1.id AND member_sn=#{mySn,jdbcType=VARCHAR}) liked,
T1.like_count likes, T1.comment_count comments FROM feeds_member_post_fandom T1 LEFT JOIN connects_member_profile T2 ON T1.member_sn = T2.sn WHERE T1.fandom_id = #{fandomId,jdbcType=BIGINT}
AND T1.member_sn=#{memberSn,jdbcType=VARCHAR}
ORDER BY T1.id DESC

SELECT T1.id, T1.post_title, T1.post_content, T1.post_newsfeeds, T1.post_time, T1.like_count likes, T1.comment_count comments,
(SELECT count(1) FROM feeds_member_post_like WHERE post_id = T1.id AND member_sn=#{mySn,jdbcType=VARCHAR}) liked,
T2.sn ui_sn, T2.nickname ui_nickname, T2.member_avatar ui_member_avatar FROM feeds_member_post_fandom T1 LEFT JOIN connects_member_profile T2 ON T1.member_sn = T2.sn WHERE T1.fandom_id = #{fandomId,jdbcType=BIGINT}
AND T1.member_sn=#{memberSn,jdbcType=VARCHAR}
ORDER BY T1.like_count DESC
