mapper.update:
UPDATE feeds_member_post_fandom
SET like_count=IFNULL(like_count,0)+1
WHERE id = #{id,jdbcType=BIGINT}

UPDATE feeds_member_post_fandom
SET like_count=CASE WHEN IFNULL(like_count,0)-1 < 0 THEN 0 ELSE IFNULL(like_count,0)-1 END
WHERE id = #{id,jdbcType=BIGINT}

UPDATE feeds_member_post_fandom
SET comment_count=IFNULL(comment_count,0)+1
WHERE id = #{id,jdbcType=BIGINT}

UPDATE feeds_member_post_fandom
SET comment_count=CASE WHEN IFNULL(comment_count,0)-1 < 0 THEN 0 ELSE IFNULL(comment_count,0)-1 END
WHERE id = #{id,jdbcType=BIGINT}

UPDATE feeds_member_post_fandom
SET vote_count = #{voteCount,jdbcType=BIGINT}
WHERE
    id = #{postId,jdbcType=BIGINT}
