mapper.update:
update feeds_member_fandom
set member_sn = #{memberSn,jdbcType=VARCHAR},
  fandom_sn = #{fandomId,jdbcType=VARCHAR},
  join_time = #{joinTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select

from feeds_member_fandom
where member_sn = #{memberSn,jdbcType=VARCHAR} and fandom_id=#{fandomId,jdbcType=VARCHAR}
